import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'To Do Task';
  task: string = ''; // Input task
  allTasks: any[] = [];
  pendingTasks: any[] = [];
  completedTasks: any[] = [];

  addTask(item: string) {
    const newTask = { id: this.allTasks.length, name: item, completed: false };
    this.allTasks.push(newTask);
    this.pendingTasks.push(newTask);
    this.task = '';
  }

  removeTask(id: number) {
    this.allTasks = this.allTasks.filter(item => item.id !== id);
    this.pendingTasks = this.pendingTasks.filter(item => item.id !== id);
    this.completedTasks = this.completedTasks.filter(item => item.id !== id);
  }

  completedTask(id: number) {
    const task = this.allTasks.find(item => item.id === id);
    if (task) {
      task.completed = true;
      this.pendingTasks = this.pendingTasks.filter(item => item.id !== id);
      this.completedTasks.push(task);
    }
  }

  filterAll() {
    this.pendingTasks = this.allTasks;
    this.completedTasks = [];
  }

  filterPending() {
    this.pendingTasks = this.allTasks.filter(item => !item.completed);
    this.completedTasks = [];
  }

  filterCompleted() {
    this.pendingTasks = this.allTasks.filter(item => item.completed);
    this.completedTasks = [];
  }
}
<h1 class="title">{{ title }}</h1>

<div class="inline">
  <input type="text" class="input" placeholder="Enter Task" [(ngModel)]="task" name="task" />

  <button class="btnAdd" (click)="addTask(task)">Add Task</button>
</div>
<div>
  <div class="sections">
    <div class="sectionA">
      <h2>
        <button (click)="filterAll()" class="transparent-button">All</button>
      </h2>
      <ul>
        <li *ngFor="let item of allTasks">
          {{ item.name }}
          <div class="action-button">
            <button (click)="removeTask(item.id)" class="action-button">Remove</button>
          </div>
          <div class="action-button">
            <button (click)="completedTask(item.id)" class="action-button">Completed</button>
          </div>
        </li>
      </ul>
    </div>
    <div class="sectionP">
      <h2>
        <button (click)="filterPending()" class="transparent-button">Pending</button>
      </h2>
      <ul>
        <li *ngFor="let item of pendingTasks">
          {{ item.name }}
        </li>
      </ul>
    </div>
    <div class="sectionC">
      <h2>
        <button (click)="filterCompleted()" class="transparent-button">Completed</button>
      </h2>
      <ul>
        <li *ngFor="let item of completedTasks">
          {{ item.name }}
        </li>
      </ul>
    </div>
  </div>
</div>
