.inline {
  display: inline-block;
  vertical-align: top; 
}

.input {
  height: 30px;
  width: 200px;
  font-size: 20px;
}

.btnAdd {
  height: 40px;
  width: 200px;
  font-size: 20px;
  border-radius: 10px;
  border: 1px solid blueviolet;
  background: blueviolet;
  color: aliceblue;
}
