import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'To Do Task';
  list: any[] = [];

  addTask(item: string) {
    this.list.push({ id: this.list.length, name: item, completed: false });
    console.warn(this.list);
  }

  removeTask(id: number) {
    console.warn(id);
    this.list = this.list.filter(item => item.id !== id);
  }

  completedTask(id: number) {
    console.warn(id);
    this.list = this.list.filter(item => item.id !== id);
  }
}
<h1>{{ title }}</h1>
<div class="inline">
  <input type="text" class="input" placeholder="Enter Task" #task />
  <button class="btnAdd" (click)="addTask(task.value)">Add Task</button>
</div>
<div>
  <div class="sections">
    <div class="sectionA">
      <h2>ALL</h2>
      <ul>
        <li *ngFor="let item of list">
          {{ item.name }}
        </li>
      </ul>
    </div>
    <div class="sectionP">
      <h2>PENDING</h2>
      <ul>
        <ng-container *ngFor="let item of list">
          <li *ngIf="!item.completed">{{ item.name }}</li>
        </ng-container>
      </ul>
    </div>
    <div class="sectionC">
      <h2>COMPLETED</h2>
      <ul>
        <ng-container *ngFor="let item of list">
          <li *ngIf="item.completed">{{ item.name }}</li>
        </ng-container>
      </ul>
    </div>
  </div>
</div>
