import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'To Do Task';
  list: any[] = [];
  showAll: boolean = true; // Indicates if we want to show all tasks

  addTask(item: string) {
    this.list.push({ id: this.list.length, name: item, completed: false });
    console.warn(this.list);
  }

  removeTask(id: number) {
    console.warn(id);
    this.list = this.list.filter(item => item.id !== id);
  }

  completedTask(id: number) {
    console.warn(id);
    const task = this.list.find(item => item.id === id);
    if (task) {
      task.completed = true;
    }
  }

  filterTasks(showAll: boolean) {
    this.showAll = showAll;
  }
  filterCompleted() {
    // Your filtering logic here
  }
  filterPending() {
    // Your filtering logic here
  }
  filterAll() {
    // Your filtering logic here
  }

}
<h1>{{ title }}</h1>
<div class="inline">
  <input type="text" class="input" placeholder="Enter Task" #task />
  <button class="btnAdd" (click)="addTask(task.value)">Add Task</button>
</div>
<div>
  <div class="sections">
    <div class="sectionA">
      <h2>
        <button (click)="filterAll()" class="transparent-button">All</button>
      </h2>
      <ul>
        <li *ngFor="let item of list">
          {{ item.name }}
          <button (click)="removeTask(item.id)" class="action-button">Remove</button>
          <button (click)="completedTask(item.id)" class="action-button">Completed</button>
        </li>
      </ul>
    </div>
    <div class="sectionP">
      <h2>
      <button (click)="filterPending()" class="transparent-button">Pending</button>
    </h2>
      <ul>
        <li *ngFor="let item of list">
          <ng-container *ngIf="!item.completed">
            {{ item.name }}
          </ng-container>
        </li>
      </ul>
    </div>

    <div class="sectionC">
      <h2>
        <button (click)="filterCompleted()" class="transparent-button">Completed</button>
      </h2>
      <ul>
        <li *ngFor="let item of list">
          <ng-container *ngIf="item.completed">
            {{ item.name }}
          </ng-container>
        </li>
      </ul>
    </div>


  </div>
</div>
